import numpy as npimport math as mth#finds the sites within the folder that have the highest cumulative flow speed cubed, as these are the ones with the most powermy_data = np.genfromtxt('site_locs.csv', delimiter=',')num_of_sites = len(my_data[:,0]) - 1top = 5 #defines the number of sites we are interested intop_sites = np.zeros([top,2]) #gives the top five tidal flow reources (prop to flow cubed) and their corresponding site                            #Find the top 5 sites new methodpowers = np.zeros([num_of_sites,2])for i in range(num_of_sites): #search over all stes    site_data = np.genfromtxt(str(1+i) + '.csv', delimiter=',')    if mth.isnan(site_data[1,6]):        a=1    else:        powers[i,1] = sum(np.power(site_data[1:,6],3))        powers[i,0]=i+1        columnIndex = 1# Sort 2D numpy array by 2nd ColumnsortedArr = powers[powers[:,columnIndex].argsort()]top_sites=sortedArr[-top:,:]# For the CFD analysis!# counter = 0         # for i in [132,133,145,146]:  #CFD site search (must do 1 less than the actual site number)#     site_data = np.genfromtxt(str(1+i) + '.csv', delimiter=',')#     if mth.isnan(site_data[1,6]):#         a=1#     else:#         top_sites[counter,0] = sum(np.power(site_data[1:,6],3))#         top_sites[counter,1] = 1 + i               #     counter += 1            print('The top Anglesey sites are: ', top_sites[:,1])print('Corresponding Flow Resources: ', top_sites[:,0])